mapper.select:
SELECT COUNT(EMP_ID)
FROM EMPLOYEE
WHERE EMP_ID = #{empId}

mapper.update:
UPDATE EMPLOYEE SET EMP_EMAIL = #{empEmail},
						OFFICE_TEL = #{officeTel}, 
						EMP_PHONE = #{empPhone}, 
						EMP_LOC = #{empLoc} 
						WHERE EMP_ID = #{empId}

mapper.select:
SELECT * FROM WORKINGDAY
WHERE EMP_NO = #{empNo}
ORDER BY TODAY

SELECT COUNT(*) FROM WORKINGDAY
WHERE TODAY = TO_CHAR(SYSDATE, 'YY/MM/DD') 
AND START_TIME IS NOT NULL AND EMP_NO = #{empNo}

SELECT COUNT(*) FROM WORKINGDAY
WHERE TODAY = TO_CHAR(SYSDATE, 'YY/MM/DD')
AND FINISH_TIME IS NOT NULL AND EMP_NO = #{empNo}

mapper.update:
UPDATE WORKINGDAY
SET STATUS = 'E'
WHERE TODAY != TO_CHAR(SYSDATE, 'YY/MM/DD') AND FINISH_TIME IS NULL AND EMP_NO = #{empNo}
