mapper.update:
UPDATE 	RESOURCE_ENTERPRISE
SET		RSC_NAME = #{rscName},
		RSC_ETC = #{rscEtc},
		STATUS = #{status}
WHERE 	RSC_NO = #{rscNo}

UPDATE 	RESOURCE_ENTERPRISE
SET		STATUS = 'N'
WHERE 	RSC_NO = #{rscNo}

UPDATE 	RESERVATION
SET		STATUS = 'N'
WHERE 	REZ_NO = #{rezNo}

UPDATE 	RESERVATION
SET		STATUS = 'C'
WHERE 	REZ_NO = #{rezNo}
